import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState, useEffect, useCallback, useMemo } from 'react';
import { createPortal } from 'react-dom';

import Navigation from './components/Navigation';
import LoadingScreen from './components/LoadingScreen';
import ExhibitSelector from './components/ExhibitSelector';
import MyExhibits from './pages/MyExhibits';
import { ExhibitProvider } from './context/ExhibitContext';
import { determinePeriod, getImageUrl, delay, fallbackArtworkData, fallbackPeriods } from './scripts/utils';

/* ---------- Portal wrapper to avoid clipping ---------- */
function SelectorPortal({ anchorRect, children, onClose, width = 320, offset = 8 }) {
  const [pos, setPos] = useState({ top: 0, left: 0 });

  // Compute a safe position within viewport
  const computePos = useCallback(() => {
    if (!anchorRect) return;
    const vw = window.innerWidth;
    const vh = window.innerHeight;

    // Prefer right-align to the anchor, but clamp inside viewport
    let left = Math.min(anchorRect.right - width, vw - width - 16);
    left = Math.max(16, left);

    let top = anchorRect.bottom + offset;
    // If near bottom, pop upward
    if (top + 360 > vh) {
      top = Math.max(16, anchorRect.top - offset - 360); // 360 = heuristic height
    }

    setPos({ top, left });
  }, [anchorRect, width, offset]);

  useEffect(() => {
    computePos();
  }, [computePos]);

  // Close on Escape / click outside
  useEffect(() => {
    function onKey(e) {
      if (e.key === 'Escape') onClose?.();
    }
    function onClick(e) {
      // If click happens outside the portal node, close it.
      // We rely on capture phase: if portal exists, we close on any body click.
      // Consumers can stopPropagation inside selector if needed.
      onClose?.();
    }
    document.addEventListener('keydown', onKey);
    // Use a short timeout so the opening click doesn't immediately close it
    const id = setTimeout(() => document.addEventListener('click', onClick, { capture: true }), 0);

    return () => {
      document.removeEventListener('keydown', onKey);
      document.removeEventListener('click', onClick, { capture: true });
      clearTimeout(id);
    };
  }, [onClose]);

  // Reposition on scroll/resize
  useEffect(() => {
    function onWin() {
      computePos();
    }
    window.addEventListener('resize', onWin);
    window.addEventListener('scroll', onWin, true);
    return () => {
      window.removeEventListener('resize', onWin);
      window.removeEventListener('scroll', onWin, true);
    };
  }, [computePos]);

  if (!anchorRect) return null;

  return createPortal(
    <div
      className="exhibit-selector-portal"
      style={{
        position: 'fixed',
        top: pos.top,
        left: pos.left,
        zIndex: 10000,
        width,
      }}
      onClick={(e) => e.stopPropagation()} // prevent outside-click handler from closing immediately
      role="dialog"
      aria-modal="true"
    >
      {children}
    </div>,
    document.body
  );
}

/* --------------------------- Home --------------------------- */
function Home() {
  const [allArtworks, setAllArtworks] = useState([]);
  const [displayedArtworks, setDisplayedArtworks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  // Selector state
  const [selectorOpen, setSelectorOpen] = useState(false);
  const [selectorArtwork, setSelectorArtwork] = useState(null);
  const [selectorAnchorRect, setSelectorAnchorRect] = useState(null);

  // Filters
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState('');
  const [availablePeriods, setAvailablePeriods] = useState([]);

  const fetchArtworks = useCallback(async (pageNum = 1, searchQuery = '', append = false) => {
    try {
      let url = `https://api.artic.edu/api/v1/artworks/search?query[term][is_public_domain]=true&limit=12&from=${(pageNum - 1) * 12}&fields=id,title,artist_display,date_display,image_id,style_titles,classification_titles,date_start,date_end`;
      if (searchQuery) url += `&q=${encodeURIComponent(searchQuery)}`;

      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch artworks');

      const data = await response.json();

      const processedArtworks = (data?.data ?? [])
        .filter((a) => a.image_id)
        .map((artwork) => ({
          id: artwork.id,
          title: artwork.title || 'Untitled',
          artist: artwork.artist_display ? artwork.artist_display.split('\n')[0] : 'Unknown Artist',
          year: artwork.date_display || 'Unknown Date',
          period: determinePeriod(artwork.date_start, artwork.date_end, artwork.style_titles),
          image_id: artwork.image_id,
          tags: [
            ...(artwork.style_titles ? artwork.style_titles.slice(0, 1) : []),
            ...(artwork.classification_titles ? artwork.classification_titles.slice(0, 1) : []),
          ]
            .filter(Boolean)
            .slice(0, 2),
        }));

      if (append) {
        setAllArtworks((prev) => {
          const merged = [...prev, ...processedArtworks];
          // Keep periods list fresh as new pages load
          const periods = [...new Set(merged.map((a) => a.period))].filter(Boolean).sort();
          setAvailablePeriods(periods.length ? periods : fallbackPeriods);
          return merged;
        });
      } else {
        setAllArtworks(processedArtworks);
        const periods = [...new Set(processedArtworks.map((a) => a.period))].filter(Boolean).sort();
        setAvailablePeriods(periods.length ? periods : fallbackPeriods);
      }

      setHasMore(processedArtworks.length === 12);
    } catch (err) {
      console.error('API Error:', err);
      if (!append) {
        setAllArtworks(fallbackArtworkData);
        setAvailablePeriods(fallbackPeriods);
      }
    }
  }, []);

  // Derived filtering
  useEffect(() => {
    let filtered = allArtworks;
    const q = searchTerm.trim().toLowerCase();

    if (q) {
      filtered = filtered.filter(
        (art) =>
          art.title.toLowerCase().includes(q) ||
          art.artist.toLowerCase().includes(q) ||
          (art.period || '').toLowerCase().includes(q)
      );
    }
    if (selectedPeriod) {
      filtered = filtered.filter((a) => a.period === selectedPeriod);
    }

    setDisplayedArtworks(filtered);
  }, [allArtworks, searchTerm, selectedPeriod]);

  // Initial load with minimum spinner time for nicer UX
  useEffect(() => {
    const load = async () => {
      setLoading(true);
      const started = Date.now();
      const MIN = 4000;

      try {
        await fetchArtworks(1);
      } catch (e) {
        console.error('Initial load error:', e);
      } finally {
        const elapsed = Date.now() - started;
        if (elapsed < MIN) await delay(MIN - elapsed);
        setLoading(false);
      }
    };
    load();
  }, [fetchArtworks]);

  const loadMore = async () => {
    if (loadingMore || !hasMore) return;
    setLoadingMore(true);
    const nextPage = page + 1;
    await fetchArtworks(nextPage, searchTerm, true);
    setPage(nextPage);
    setLoadingMore(false);
  };

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedPeriod('');
  };

  // Open selector anchored to the clicked button
  const openSelector = (artwork, e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setSelectorArtwork(artwork);
    setSelectorAnchorRect(rect);
    setSelectorOpen(true);
  };

  const closeSelector = () => {
    setSelectorOpen(false);
    setSelectorArtwork(null);
    setSelectorAnchorRect(null);
  };

  const resultsLabel = useMemo(() => {
    const n = displayedArtworks.length;
    return `Showing ${n} artwork${n === 1 ? '' : 's'}`;
  }, [displayedArtworks.length]);

  if (loading) return <LoadingScreen />;

  return (
    <div className="App">
      <Navigation currentPage="home" />

      {/* Filter Controls */}
      <section className="filter-controls" aria-label="Search and filter artworks">
        <div className="search-bar">
          <label htmlFor="artwork-search" className="visually-hidden">
            Search artworks by title, artist, or period
          </label>
          <input
            id="artwork-search"
            type="text"
            placeholder="Search by title, artist, or period..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
            aria-describedby="search-help"
          />
          <div id="search-help" className="visually-hidden">
            Enter keywords to search through artwork titles, artists, and time periods
          </div>

          <label htmlFor="period-filter" className="visually-hidden">
            Filter by time period
          </label>
          <select
            id="period-filter"
            value={selectedPeriod}
            onChange={(e) => setSelectedPeriod(e.target.value)}
            className="filter-select"
            aria-describedby="period-help"
          >
            <option value="">All Periods</option>
            {availablePeriods.map((period) => (
              <option key={period} value={period}>
                {period}
              </option>
            ))}
          </select>
          <div id="period-help" className="visually-hidden">
            Choose a specific time period to filter artworks
          </div>

          {(searchTerm || selectedPeriod) && (
            <button
              onClick={clearFilters}
              className="clear-filters-btn"
              aria-label="Clear all search filters and show all artworks"
            >
              Clear Filters
            </button>
          )}
        </div>
      </section>

      {/* Results count */}
      <div className="results-info" role="status" aria-live="polite">
        {resultsLabel}
      </div>

      {/* Gallery */}
      <main className="gallery" role="main" aria-label="Artwork gallery">
        {displayedArtworks.map((artwork) => (
          <article key={artwork.id} className="artwork-card">
            <div className="artwork-image" aria-hidden="true">
              {artwork.image_id ? (
                <>
                  <img
                    src={getImageUrl(artwork.image_id)}
                    alt=""
                    loading="lazy"
                    onError={(e) => {
                      e.currentTarget.classList.add('hidden');
                      const ph = e.currentTarget.parentElement?.querySelector('.no-image');
                      if (ph) ph.classList.remove('hidden');
                    }}
                  />
                  <div className="no-image hidden" aria-hidden="true">
                    No Image Available
                  </div>
                </>
              ) : (
                <div className="no-image">No Image Available</div>
              )}
              <button
                className="bookmark-btn"
                onClick={(e) => openSelector(artwork, e)}
                aria-label={`Add ${artwork.title} to my exhibits`}
                title="Add to My Exhibits"
              >
                +
              </button>
            </div>

            <div className="artwork-info">
              <h3 className="artwork-title">{artwork.title}</h3>
              <p className="artwork-meta">
                {artwork.artist} • {artwork.year}
              </p>
              <div className="artwork-tags" aria-label="Artwork categories">
                {artwork.tags.map((tag, index) => (
                  <span key={`${artwork.id}-${tag}-${index}`} className={`tag ${tag.toLowerCase().replace(/\s+/g, '-')}`}>
                    {tag.toUpperCase()}
                  </span>
                ))}
              </div>
            </div>
          </article>
        ))}
      </main>

      {/* Load More Button */}
      {hasMore && !searchTerm && !selectedPeriod && (
        <div className="load-more-container">
          <button
            onClick={loadMore}
            disabled={loadingMore}
            className="load-more-btn"
            aria-label={loadingMore ? 'Loading more artworks' : 'Load more artworks'}
          >
            {loadingMore ? 'Loading...' : 'Load More'}
          </button>
          <div className="visually-hidden">
            Click to load 12 additional artworks from the Art Institute of Chicago collection
          </div>
        </div>
      )}

      {/* Selector Portal (renders outside clipping containers) */}
      {selectorOpen && selectorArtwork && selectorAnchorRect && (
        <SelectorPortal anchorRect={selectorAnchorRect} onClose={closeSelector} width={320}>
          <ExhibitSelector artwork={selectorArtwork} onClose={closeSelector} />
        </SelectorPortal>
      )}
    </div>
  );
}

/* --------------------------- App --------------------------- */
function App() {
  return (
    <ExhibitProvider>
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/my-exhibits" element={<MyExhibits />} />
        </Routes>
      </Router>
    </ExhibitProvider>
  );
}

export default App;
