import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Navigation from '../components/Navigation';
import ExhibitModal from '../components/ExhibitModal';
import { useExhibit } from '../context/ExhibitContext';
import { getImageUrl } from '../scripts/utils';
import './MyExhibits.css';

function MyExhibits() {
  const { exhibits, createExhibit, updateExhibit, deleteExhibit, removeArtworkFromExhibit } = useExhibit();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingExhibit, setEditingExhibit] = useState(null);
  const [selectedExhibit, setSelectedExhibit] = useState(null);

  const handleCreateExhibit = (exhibitData) => {
    createExhibit(exhibitData.name, exhibitData.description);
    setIsModalOpen(false);
  };

  const handleEditExhibit = (exhibitData) => {
    updateExhibit(editingExhibit.id, exhibitData);
    setEditingExhibit(null);
    setIsModalOpen(false);
  };

  const handleDeleteExhibit = (exhibitId) => {
    if (window.confirm('Are you sure you want to delete this exhibit? This action cannot be undone.')) {
      deleteExhibit(exhibitId);
      if (selectedExhibit?.id === exhibitId) {
        setSelectedExhibit(null);
      }
    }
  };

  const openEditModal = (exhibit) => {
    setEditingExhibit(exhibit);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setEditingExhibit(null);
  };

  return (
    <div className="my-exhibits">
      <Navigation currentPage="my-exhibits" />

      <main className="exhibits-container" role="main">
        <header className="exhibits-header">
          <div className="header-content">
            <div className="header-text">
              <h1>My Exhibits</h1>
              <p>Your curated collections and saved artworks</p>
            </div>
            {exhibits.length > 0 && (
              <button 
                className="create-exhibit-btn primary"
                onClick={() => setIsModalOpen(true)}
              >
                + Create New Exhibit
              </button>
            )}
          </div>
        </header>

        <section className="exhibits-content" aria-label="Exhibits content">
          {exhibits.length > 0 ? (
            <div className="exhibits-layout">
              <div className="exhibits-sidebar">
                <h2>Collections ({exhibits.length})</h2>
                <div className="exhibits-list">
                  {exhibits.map((exhibit) => (
                    <div 
                      key={exhibit.id}
                      className={`exhibit-item ${selectedExhibit?.id === exhibit.id ? 'active' : ''}`}
                      onClick={() => setSelectedExhibit(exhibit)}
                    >
                      <div className="exhibit-item-content">
                        <h3>{exhibit.name}</h3>
                        <p>{exhibit.artworks.length} artwork{exhibit.artworks.length !== 1 ? 's' : ''}</p>
                      </div>
                      <div className="exhibit-actions">
                        <button 
                          className="edit-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            openEditModal(exhibit);
                          }}
                          aria-label={`Edit ${exhibit.name}`}
                        >
                          ‚úèÔ∏è
                        </button>
                        <button 
                          className="delete-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteExhibit(exhibit.id);
                          }}
                          aria-label={`Delete ${exhibit.name}`}
                        >
                          üóëÔ∏è
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="exhibit-details">
                {selectedExhibit ? (
                  <div className="exhibit-view">
                    <div className="exhibit-header">
                      <h2>{selectedExhibit.name}</h2>
                      {selectedExhibit.description && (
                        <p className="exhibit-description">{selectedExhibit.description}</p>
                      )}
                      <div className="exhibit-meta">
                        <span>{selectedExhibit.artworks.length} artwork{selectedExhibit.artworks.length !== 1 ? 's' : ''}</span>
                        <span>Created {new Date(selectedExhibit.createdAt).toLocaleDateString()}</span>
                      </div>
                    </div>

                    {selectedExhibit.artworks.length > 0 ? (
                      <div className="exhibit-artworks">
                        {selectedExhibit.artworks.map((artwork) => (
                          <article key={artwork.id} className="exhibit-artwork-card">
                            <div className="artwork-image">
                              {artwork.image_id ? (
                                <img 
                                  src={getImageUrl(artwork.image_id)} 
                                  alt={`${artwork.title} by ${artwork.artist}, ${artwork.year}`}
                                  onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.nextElementSibling.style.display = 'flex';
                                  }}
                                />
                              ) : (
                                <div className="no-image" role="img" aria-label="No image available">
                                  No Image Available
                                </div>
                              )}
                              <button 
                                className="remove-btn"
                                onClick={() => removeArtworkFromExhibit(selectedExhibit.id, artwork.id)}
                                aria-label={`Remove ${artwork.title} from exhibit`}
                                title="Remove from exhibit"
                              >
                                √ó
                              </button>
                            </div>
                            <div className="artwork-info">
                              <h3 className="artwork-title">{artwork.title}</h3>
                              <p className="artwork-meta">{artwork.artist} ‚Ä¢ {artwork.year}</p>
                              <div className="artwork-tags">
                                {artwork.tags.map((tag, index) => (
                                  <span key={index} className={`tag ${tag.toLowerCase().replace(/\s+/g, '-')}`}>
                                    {tag.toUpperCase()}
                                  </span>
                                ))}
                              </div>
                            </div>
                          </article>
                        ))}
                      </div>
                    ) : (
                      <div className="empty-exhibit">
                        <div className="empty-icon">üñºÔ∏è</div>
                        <h3>No artworks yet</h3>
                        <p>Browse artworks and add them to this exhibit.</p>
                        <Link to="/" className="cta-button">Browse Artworks</Link>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="no-selection">
                    <div className="no-selection-content">
                      <div className="empty-icon">üëà</div>
                      <h3>Select an exhibit</h3>
                      <p>Choose an exhibit from the list to view its artworks.</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-icon">üé®</div>
              <h2>No exhibits yet</h2>
              <p>Start curating your first exhibit by creating a collection and adding artworks.</p>
              <button 
                className="cta-button"
                onClick={() => setIsModalOpen(true)}
              >
                Create Your First Exhibit
              </button>
            </div>
          )}
        </section>
      </main>

      <ExhibitModal
        isOpen={isModalOpen}
        onClose={closeModal}
        onSave={editingExhibit ? handleEditExhibit : handleCreateExhibit}
        exhibit={editingExhibit}
        mode={editingExhibit ? 'edit' : 'create'}
      />
    </div>
  );
}

export default MyExhibits;
